import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Body, Text } from './style';
import { Grid, Container } from 'semantic-ui-react';


const PokemonList = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async() => {
    const url = 'https://pokeapi.co/api/v2/pokemon/';
    const pokeData = await axios.get(url);
    const pokeList = pokeData?.data?.results || [];

    setData(pokeList);
    console.log(pokeData)
    console.log(pokeList)
  }

  const pokeList = () => {
    return (
      <Grid>
        <Grid.Row>
          {
            data.map((data, index) => {
              const pokemonIndex = index + 1;
              const { name } = data;
              return (
                <Grid.Column width={5} key={pokemonIndex}>
                  <Card>
                    <Text header># {pokemonIndex}</Text>
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemonIndex}.png`}/>
                    <Text>{name}</Text>
                  </Card>
                </Grid.Column>
              );
            })
          }
        </Grid.Row>
      </Grid>
    );
  }

  return (
    <Body>
      <Container>
        {pokeList()}
      </Container>
    </Body>
  );
}

export default PokemonList;
