import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Body, DivWrap, Head, Wrapper, Text, Base } from './style';
import { Container, Grid, Card, Progress } from 'semantic-ui-react';

const PokemonDetails = ({ match }) => {
  const [pokeData, setPokeData] = useState([]);
  const [pokeStatusList, setPokeStatusList] = useState({
    hp: '',
    attack: '',
    defense: '',
    speed: '',
    specialAttack: '',
    specialDefense: ''
  });
  const [desc, setDesc] = useState('');

  useEffect(() => {
    getPokemonDetails();
  },[])

  const getPokemonDetails = async() => {
    const { id } = match.params;
    const rawData = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    const detailsData = rawData?.data || [];
    const rawSpecies = await axios.get(`https://pokeapi.co/api/v2/pokemon-species/${id}`);
    const description = rawSpecies?.data?.flavor_text_entries;
    console.log(desc);

    description.map(stat => {
      if(stat.version.name === 'alpha-sapphire');
      setDesc(stat.flavor_text)
    });

    let {
      hp,
      attack,
      defense,
      speed,
      specialAttack,
      specialDefense
    } = ''

    detailsData.stats.map(stat => {
      switch(stat.stat.name) {
        case 'hp':
          hp = stat.base_stat;
          break;
        case 'attack':
          attack = stat.base_stat;
          break;
        case 'defense':
          defense = stat.base_stat;
          break;
        case 'speed':
          speed = stat.base_stat;
          break;
        case 'special-attack':
          specialAttack = stat.base_stat;
          break;
        case 'special-defense':
          specialDefense = stat.base_stat;
          break;
        default:
          break;
      }
    });

    setPokeData(detailsData);
    setPokeStatusList({
      hp,
      attack,
      defense,
      speed,
      specialAttack,
      specialDefense
    });
  }

  const pokemonDetail = () => {
    const { hp, attack, defense, speed, specialAttack, specialDefense } = pokeStatusList;
    return (
      <Grid>
        <Grid.Row>
          <Grid.Column width={4}>
            <Grid.Row>
              <Base>Hp</Base>
            </Grid.Row>
            <Grid.Row>
              <Base>Attack</Base>
            </Grid.Row>
            <Grid.Row>
              <Base>Defense</Base>
            </Grid.Row>
            <Grid.Row>
              <Base>Speed</Base>
            </Grid.Row>
            <Grid.Row>
              <Base>SpecialAttack</Base>
            </Grid.Row>
            <Grid.Row>
              <Base>SpecialDefense</Base>
            </Grid.Row>
          </Grid.Column>
          <Grid.Column width={10}>
            <Grid.Row>
              <Progress progress='value' value={hp} color='purple' size='small'/>
            </Grid.Row>
            <Grid.Row>
              <Progress progress='value' value={attack} color='purple' size='small'/>
            </Grid.Row>
              <Grid.Row>
              <Progress progress='value' value={defense} color='purple' size='small'/>
            </Grid.Row>
            <Grid.Row>
              <Progress progress='value' value={speed} color='purple' size='small'/>
            </Grid.Row>
            <Grid.Row>
              <Progress progress='value' value={specialAttack} color='purple' size='small'/>
            </Grid.Row>
            <Grid.Row>
              <Progress progress='value' value={specialDefense} color='purple' size='small'/>
            </Grid.Row>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    )
  }

  const pokeBaseStatus = () => {
    const { id, name } = pokeData
    const url = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`;
    return (
      <DivWrap>
        <Grid columns={16}>
          <Grid.Column width={5}>
            <img src={url} alt="pokemon" wrapped='true'/>
          </Grid.Column>
          <Grid.Column width={11}>
            <Text>{name}</Text>
            {pokemonDetail()}
          </Grid.Column>
        </Grid>
      </DivWrap>
    );
  }

  const pokeStatus = () => {
    const { id } = pokeData;
    return (
      <Wrapper>
        <Card style={{width: '100%'}}>
          <Card.Content extra style={{backgroundColor: 'rgba(192, 192, 192, 0.2)',}}>
            <Head># {id}</Head>
          </Card.Content>
            {pokeBaseStatus()}
            <p>hahahahah</p>
          <Card.Content>
            <Card.Header style={{textTransform: 'capitalize'}}>
              ghahahah
            </Card.Header>
            <Card.Meta>
              <span className='date'>Joined in 2015</span>
            </Card.Meta>
            <Card.Description>
              Matthew is a musician living in Nashville.
            </Card.Description>
          </Card.Content>
        </Card>
      </Wrapper>     
    );
  }

  return (
    <Body>
      <Container>
        <div style={{height: '100vh'}}>
          {pokeStatus()}
          {console.log(pokeStatusList)}
        </div>
      </Container>
    </Body>
  )
}

export default PokemonDetails
