import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Body, Card, Head } from './style';
import { Container, Grid } from 'semantic-ui-react';

const PokemonDetails = ({ match }) => {
  const [pokeData, setPokeData] = useState([]);

  useEffect(() => {
    getPokemonDetails();
  },[])

  const getPokemonDetails = async() => {
    const { id } = match.params;
    const rawData = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    const detailsData = rawData?.data || [];
    setPokeData(detailsData);
  }

  const header = () => {
    const { id } = pokeData
    return (
      <Grid>
        <Grid.Column>
          <Head>{id}</Head>
        </Grid.Column>
        <Grid.Column>
          {/* <div style={{display: 'flex', justifyContent: 'flex-end'}}>
            <Card>
              AlOHADANCE
            </Card>
          </div> */}
        </Grid.Column>
      </Grid>
    );
  }

  const pokeStatus = () => {
    const { id } = pokeData
    return (
      <Card>
        {header()}
        <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`} alt="img"/>
      </Card>
    );
  }

  return (
    <Body>
      <Container>
        <div style={{height: '100vh'}}>
          nama aku achmad
          {pokeStatus()}
          {console.log(pokeData)}
        </div>
      </Container>
    </Body>
  )
}

export default PokemonDetails
