import styled from 'styled-components';

export const Card = styled.div({
  boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2)',
  width: '100%',
  position: 'relative',
  '& img': {
    objectFit: 'cover',
    display: 'block',
    margin: '20px auto'
  },
  '& p': {
    textAlign: 'center',
    fontWeight: 'bold',
    textTransform: 'capitalize'
  }
});

export const Body = styled.div({
  backgroundColor: '#f4f4f4'
})
