import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Body, Text } from './style';
import { Grid, Container } from 'semantic-ui-react';


const PokemonList = () => {
  const [data, setData] = useState([]);
  const [activePage, setActivePage] = useState('https://pokeapi.co/api/v2/pokemon/');
  const [nextPage, setNextPage] = useState([]);
  const [prevPage, setPrevPage] = useState([]);

  useEffect(() => {
    fetchData();
  }, [activePage]);

  const fetchData = async() => {
    const pokeData = await axios.get(activePage);
    const pokeList = pokeData?.data || [];

    setData(pokeList.results);
    setNextPage(pokeList.next);
    setPrevPage(pokeList.previous);
  }

  const handleNextPage = () => {
    setActivePage(nextPage);
  }

  const pokeList = () => {
    return (
      <Grid>
        <Grid.Row>
          {
            data.map(data => {
              const { name, url } = data;
              const pokeId = url.split('').splice(-3, 2).join('').replace('/','');
              return (
                <Grid.Column width={4} key={pokeId}>
                  <Card>
                    <Text header># {pokeId}</Text>
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokeId}.png`}/>
                    <Text>{name}</Text>
                  </Card>
                </Grid.Column>
              );
            })
          }
        </Grid.Row>
      </Grid>
    );
  }

  const button = () => <button style={{fontSize: 100, backgroundColor: 'red'}} onClick={handleNextPage}>click aku</button>

  return (
    <Body>
      {console.log(data)}
      {console.log(nextPage)}
      <Container>
        {pokeList()}
        {button()}
      </Container>
    </Body>
  );
}

export default PokemonList;
