import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Body, DivWrap, Head, Wrapper, Text } from './style';
import { Container, Grid, Card } from 'semantic-ui-react';

const PokemonDetails = ({ match }) => {
  const [pokeData, setPokeData] = useState([]);
  const [pokeStatusList, setPokeStatusList] = useState({
    hp: '',
    attack: '',
    defense: '',
    speed: '',
    specialAttack: '',
    specialDefense: ''
  });

  useEffect(() => {
    getPokemonDetails();
  },[])

  const getPokemonDetails = async() => {
    const { id } = match.params;
    const rawData = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    const detailsData = rawData?.data || [];
    const rawSpecies = await axios.get(`https://pokeapi.co/api/v2/pokemon-species/${id}`);
    console.log(detailsData)

    let {
      hp,
      attack,
      defense,
      speed,
      specialAttack,
      specialDefense
    } = ''

    detailsData.stats.map(stat => {
      switch(stat.stat.name) {
        case 'hp':
          hp = stat.base_stat;
          break;
        case 'attack':
          attack = stat.base_stat;
          break;
        case 'defense':
          defense = stat.base_stat;
          break;
        case 'speed':
          speed = stat.base_stat;
          break;
        case 'special-attack':
          specialAttack = stat.base_stat;
          break;
        case 'special-defense':
          specialDefense = stat.base_stat;
          break;
        default:
          break;
      }
    });

    setPokeData(detailsData);
    setPokeStatusList({
      hp,
      attack,
      defense,
      speed,
      specialAttack,
      specialDefense
    });
  }

  const pokemonDetail = () => {
    const { hp, attack, defense, speed, specialAttack, specialDefense } = pokeStatusList;
    return (
      <Grid>
        <Grid.Row>
          <Grid.Column width={4}>
            <Grid.Row>
              <Text>hp</Text>
            </Grid.Row>
            <Grid.Row>
              attack
            </Grid.Row>
            <Grid.Row>
              defense
            </Grid.Row>
            <Grid.Row>
              speed
            </Grid.Row>
            <Grid.Row>
              specialAttack
            </Grid.Row>
            <Grid.Row>
              specialDefense
            </Grid.Row>
          </Grid.Column>
          <Grid.Column width={4}>
            hallo
            ini aku
          </Grid.Column>
        </Grid.Row>
      </Grid>
    )
  }

  const pokeBaseStatus = () => {
    const { id, name } = pokeData
    const url = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`;
    return (
      <DivWrap>
        <Grid columns={16}>
          <Grid.Column width={5}>
            <img src={url} alt="pokemon" wrapped='true'/>
          </Grid.Column>
          <Grid.Column width={11}>
            <p>{name}</p>
            {pokemonDetail()}
          </Grid.Column>
        </Grid>
      </DivWrap>
    );
  }

  const pokeStatus = () => {
    const { id, name } = pokeData;
    return (
      <Wrapper>
        <Card style={{width: '100%'}}>
          <Card.Content extra style={{backgroundColor: 'rgba(192, 192, 192, 0.2)',}}>
            <Head>{id}</Head>
          </Card.Content>
            {pokeBaseStatus()}
          <Card.Content>
            <Card.Header style={{textTransform: 'capitalize'}}>
            </Card.Header>
            <Card.Meta>
              <span className='date'>Joined in 2015</span>
            </Card.Meta>
            <Card.Description>
              Matthew is a musician living in Nashville.
            </Card.Description>
          </Card.Content>
        </Card>
      </Wrapper>     
    );
  }

  return (
    <Body>
      <Container>
        <div style={{height: '100vh'}}>
          {pokeStatus()}
          {console.log(pokeStatusList)}
        </div>
      </Container>
    </Body>
  )
}

export default PokemonDetails
