import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Body, DivWrap, Head, Wrapper } from './style';
import { Container, Grid, Card } from 'semantic-ui-react';

const PokemonDetails = ({ match }) => {
  const [pokeData, setPokeData] = useState([]);
  const [pokeSpecies, setPokeSpecies] = useState('');

  useEffect(() => {
    getPokemonDetails();
  },[])

  const getPokemonDetails = async() => {
    const { id } = match.params;
    const rawData = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    const detailsData = rawData?.data || [];
    const rawSpecies = await axios.get(`https://pokeapi.co/api/v2/pokemon-species/${id}`);
    console.log(rawData)

    let {
      hp,
      attack,
      defense,
      speed,
      specialAttack,
      specialDefense
    } = ''

    // rawSpecies.map(stat => {
    //   switch(stat.stat.name) {
    //     case 'hp':
    //       hp = stat.base-stat;
    //       break;
    //     case 'attack':
    //       attack = stat['base-stat'];
    //       break;
    //     case 'defense':
    //       defense = stat['base-stat'];
    //       break;
    //     case 'speed':
    //       speed = stat['base-stat'];
    //       break;
    //     case 'special-attack':
    //       specialAttack = stat['base-stat'];
    //       break;
    //     case 'special-defense':
    //       specialDefense = stat['base-stat'];
    //       break;
    //     default:
    //   }
    // })

    setPokeData(detailsData);
    setPokeSpecies(hp, attack, defense, specialAttack, specialDefense, speed)
  }

  const pokemonDetail = () => {
    return (
      <Grid>
        <Grid.Row>
          <Grid.Column width={4}>
            hallo
            ini aku
          </Grid.Column>
          <Grid.Column width={4}>
            hallo
            ini aku
          </Grid.Column>
        </Grid.Row>
      </Grid>
    )
  }

  const pokeBaseStatus = () => {
    const { id, name } = pokeData
    const url = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`;
    return (
      <DivWrap>
        <Grid columns={16}>
          <Grid.Column width={5}>
            <img src={url} alt="pokemon" wrapped='true'/>
          </Grid.Column>
          <Grid.Column width={11}>
            <p>{name}</p>
            {pokemonDetail()}
          </Grid.Column>
        </Grid>
      </DivWrap>
    );
  }

  const pokeStatus = () => {
    const { id, name } = pokeData;
    return (
      <Wrapper>
        <Card style={{width: '100%'}}>
          <Card.Content extra style={{backgroundColor: 'rgba(192, 192, 192, 0.2)',}}>
            <Head>{id}</Head>
          </Card.Content>
          {pokeBaseStatus()}
          <Card.Content>
            <Card.Header style={{textTransform: 'capitalize'}}>
            </Card.Header>
            <Card.Meta>
              <span className='date'>Joined in 2015</span>
            </Card.Meta>
            <Card.Description>
              Matthew is a musician living in Nashville.
            </Card.Description>
          </Card.Content>
        </Card>
      </Wrapper>     
    );
  }

  return (
    <Body>
      <Container>
        <div style={{height: '100vh'}}>
          {pokeStatus()}
        </div>
      </Container>
    </Body>
  )
}

export default PokemonDetails
