import axios from 'axios';
import { Body } from './style';
import { Container, Grid } from 'semantic-ui-react';

const PokemonDetails = ({ match }) => {
  const [pokeData, setPokeData] = useState([]);

  useEffect(() => {
    getPokemonDetails();
  },[])

  const getPokemonDetails = async() => {
    const { id } = match.params;
    const rawData = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    const detailsData = rawData?.data || [];
    setPokeData(detailsData);
  }

  const pokeStatus = () => {
    const { id } = pokeData
    return (
      <>
        <img style={{width: '100%'}} src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`} alt="img"/>
      </>
    )
  }

  return (
    <Body>
      <Container>
        <div style={{width: '100vw', height: '100vh'}}>
          nama aku achmad
          {pokeStatus()}
          {console.log(pokeData)}
        </div>
      </Container>
    </Body>
  )
}

export default PokemonDetails
