import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Body, Text } from './style';
import { Grid, Container } from 'semantic-ui-react';


const PokemonList = () => {
  const [data, setData] = useState([]);
  const [activePage, setActivePage] = useState('https://pokeapi.co/api/v2/pokemon/');
  const [nextPage, setNextPage] = ([]);

  useEffect(() => {
    fetchData();
    fetchNextPage();
  }, [activePage]);

  const fetchData = async() => {
    const pokeData = await axios.get(activePage);
    const pokeList = pokeData?.data?.results || [];

    setData(pokeList);
    console.log(pokeData)
    console.log(pokeList)
  }

  const fetchNextPage = async() => {
    const url = 'https://pokeapi.co/api/v2/pokemon/?offset=20&limit=20';
    const nextPage = await axios.get(url);
    const pokeNext = nextPage?.data?.results || [];

    setNextPage(pokeNext);
  }

  const heuh = () => {
    setActivePage(nextPage);
  }

  const pokeList = () => {
    return (
      <Grid>
        <Grid.Row>
          {
            data.map((data, index) => {
              const pokemonIndex = index + 1;
              const { name } = data;
              return (
                <Grid.Column width={4} key={pokemonIndex}>
                  <Card>
                    <Text header># {pokemonIndex}</Text>
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemonIndex}.png`}/>
                    <Text>{name}</Text>
                  </Card>
                </Grid.Column>
              );
            })
          }
        </Grid.Row>
      </Grid>
    );
  }

  const button = () => <button onClick={heuh}>click aku</button>

  return (
    <Body>
      <Container>
        {pokeList()}
        {button()}
      </Container>
    </Body>
  );
}

export default PokemonList;
