import styled from 'styled-components';
import pokemon from '../../Pokemon.jpg';

export const Card = styled.div({
  width: '100%',
  position: 'relative',
  backgroundColor: '#f4f4f4',
  opacity: 0.9,
  borderRadius: 8,
  margin: '16px 0',
  transition: 'transform',
  ':hover': {
    transform: 'scale(1.1)',
    opacity: 1
  },
  '& img': {
    objectFit: 'cover',
    display: 'block',
    margin: '20px auto'
  }
});

export const Text = styled.p(props => ({
  textAlign: props.header? 'align-left' : 'center',
  fontWeight: 'bold',
  padding: props.header? '12px 0px 0px' : '0px 0px 16px',
  textTransform: 'capitalize',
  textIndent: props.header? '2em' : 0,
  fontSize: props.header? 20 : '',
  border: '1px solid'
}))

export const Body = styled.div({
  background: `url(${pokemon}) no-repeat center`,
  backgroundSize: 'cover'
})
