import styled from 'styled-components';

export const Card = styled.div({
  boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2)',
  width: '100%',
  position: 'relative',
  '& img': {
    objectFit: 'cover',
    top: 0,
    right: 0,
    position: 'absolute'
  },
  '& p': {
    textAlign: 'center',
    fontWeight: 'bold',
    textTransform: 'capitalize'
  }
});
