import React, { useState, useEffect } from "react";
import axios from "axios";
import { Grid } from "semantic-ui-react";

const GridExampleDividedNumber = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    const dataTai = await axios.get("https://pokeapi.co/api/v2/pokemon/");
    const iniBaru = dataTai?.data?.results || [];
    setData(iniBaru);
  };
  return (
    <Grid columns={3}>
      <Grid.Row>
        {data.map((data, index) => {
          const pokemonIndex = index + 1;
          return (
            <>
              <Grid.Column>
                <img
                  src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemonIndex}.png`}
                />
                {data.name}
              </Grid.Column>
            </>
          );
        })}
      </Grid.Row>
    </Grid>
  );
};

export default GridExampleDividedNumber;
