import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Body, Cardd, Head, Wrapper } from './style';
import { Container, Grid, Card, Image, Icon } from 'semantic-ui-react';

const PokemonDetails = ({ match }) => {
  const [pokeData, setPokeData] = useState([]);

  useEffect(() => {
    getPokemonDetails();
  },[])

  const getPokemonDetails = async() => {
    const { id } = match.params;
    const rawData = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    const detailsData = rawData?.data || [];
    setPokeData(detailsData);
  }

  const pokeBaseStatus = () => {
    const { id } = pokeData
        const url = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`;
    return (
      <div style={{borderTop: '2px solid'}}>
      <Grid columns={16}>
        <Grid.Column width={5}>
          <img style={{backgroundColor: '#fff', objectFit: 'contain', width: 450, objectPosition: '-30px 0'}} src={url} wrapped/>
        </Grid.Column>
        <Grid.Column width={11}>
          <p style={{wordWrap: 'break-word'}}>alohadance adsadasdasdsadsadsadasdadasdasdasdasdasdasdasdsadasdasdasdsadasdasdasdasdasdasdasdasdasdasdasd</p>
        </Grid.Column>
      </Grid>
      </div>
    )
  }

  const pokeStatus = () => {
    const { id, name } = pokeData;

    return (
      <Wrapper>
        <Card style={{width: '100%'}}>
          <Card.Content extra style={{backgroundColor: 'rgba(192, 192, 192, 0.2)',}}>
            <Head>{id}</Head>
          </Card.Content>
          {pokeBaseStatus()}
          <Card.Content>
            <Card.Header style={{textTransform: 'capitalize'}}>{name}</Card.Header>
            <Card.Meta>
              <span className='date'>Joined in 2015</span>
            </Card.Meta>
            <Card.Description>
              Matthew is a musician living in Nashville.
            </Card.Description>
          </Card.Content>
        </Card>
      </Wrapper>     
    );
  }

  return (
    <Body>
      <Container>
        <div style={{height: '100vh'}}>
          {pokeStatus()}
          {console.log(pokeData)}
        </div>
      </Container>
    </Body>
  )
}

export default PokemonDetails
