import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Body, Text } from './style';
import { Grid, Container } from 'semantic-ui-react';
import { BrowserRouter as Router, Link, Route, Switch } from 'react-router-dom';
import PokemonDetails from '../pokemonDetails/index';

const PokemonList = () => {
  const [data, setData] = useState([]);
  const [activePage, setActivePage] = useState('https://pokeapi.co/api/v2/pokemon/');
  const [nextPage, setNextPage] = useState([]);
  const [prevPage, setPrevPage] = useState([]);

  useEffect(() => {
    fetchData();
  }, [activePage]);

  const fetchData = async() => {
    const pokeData = await axios.get(activePage);
    const pokeList = pokeData?.data || [];

    setData(pokeList.results);
    setNextPage(pokeList.next);
    setPrevPage(pokeList.previous);
  }

  const handleNextPage = () => {
    setActivePage(nextPage);
  }

  const handlePrevPage = () => {
    setActivePage(prevPage);
  }

  const pokeList = () => {
    return (
      <Grid>
        <Grid.Row>
          {
            data.map(data => {
              const { name, url } = data;
              const pokeId = url.split('').splice(-3, 2).join('').replace('/','');
              return (
                <Grid.Column width={4} key={pokeId}>
                  <Card>
                    <Text header># {pokeId}</Text>
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokeId}.png`} alt="img"/>
                    <Text>{name}</Text>
                  </Card>
                </Grid.Column>
              );
            })
          }
        </Grid.Row>
      </Grid>
    );
  }

  const button = () => <button style={{fontSize: 100, backgroundColor: 'red'}} onClick={handleNextPage}>click aku</button>
  const button2 = () => <button style={{fontSize: 100, backgroundColor: 'blue'}} onClick={handlePrevPage}>aku disini</button>
  const button3 = () => {
    return (
        <Router>
          <Link>
              <Route exact path="/home" component={PokemonDetails}>
                GAHAHAHHA
              </Route>
          </Link>
        </Router>
    )
  }

  // const tai = () => {
  //   return (
  //     <Router>
  //       <div className="App" style={{ background: `url(${backgroundImage})` }}>
  //         <NavBar />
  //         <div className="container">
  //           <Switch>
  //             <Route exact path="/" component={Dashboard} />
  //             <Route exact path="/pokemon/:pokemonIndex" component={Pokemon} />
  //           </Switch>
  //         </div>
  //       </div>
  //     </Router>
  //   )
  // }


  return (
    <Router>
      <Body>
        {console.log(data)}
        {console.log(nextPage)}
        <Container>
          {/* {button3()}
          {pokeList()}
          {button()}
          {button2()} */}
          <Switch>
            <Route exact path='/'/>
            <Route exact path='/pokemon/balbla' component={PokemonDetails}/>
          </Switch>
        </Container>
      </Body>
    </Router>
  );
}

export default PokemonList;
