import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Body, Cardd, Head, Line } from './style';
import { Container, Grid, Card, Image, Icon } from 'semantic-ui-react';

const PokemonDetails = ({ match }) => {
  const [pokeData, setPokeData] = useState([]);

  useEffect(() => {
    getPokemonDetails();
  },[])

  const getPokemonDetails = async() => {
    const { id } = match.params;
    const rawData = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    const detailsData = rawData?.data || [];
    setPokeData(detailsData);
  }

  const pokeStatus = () => {
    const { id } = pokeData
    return (
      <Card>
        <Card.Content extra>
          {id}
        </Card.Content>
        <Image style={{backgroundColor: '#fff', borderTop: '2px solid'}} src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`} wrapped/>
        <Card.Content>
          <Card.Header>Matthew</Card.Header>
          <Card.Meta>
            <span className='date'>Joined in 2015</span>
          </Card.Meta>
          <Card.Description>
            Matthew is a musician living in Nashville.
          </Card.Description>
        </Card.Content>

      </Card>
    );
  }

  return (
    <Body>
      <Container>
        <div style={{height: '100vh'}}>
          {pokeStatus()}
          {console.log(pokeData)}
        </div>
      </Container>
    </Body>
  )
}

export default PokemonDetails
